    var data = [];
    for (var i = 1; i &lt;= NumPoints; i++) {
        var j = i * distance / NumPoints;
        data.push([j , amplitude_m3 * Math.sin(j * 2*Math.PI / wavelength_m3 + time_m3)]);
    }
    return data;
