p1_t = {
    var p1_t = [];
    for (var t =1; t &lt;= observation_time; t++){
        var j = d1x_m3 * distance / NumPoints;
        var k = t / wavelength_m3;
        p1_t.push([k,amplitude_m3 * Math.sin(j * 2*Math.PI / wavelength_m3 + k)]);
    }
    return p1_t;
}









